import{parse}from"querystring";const parsec=fn=>async(req,_res)=>{if(["POST","PUT","PATCH","DELETE"].includes(req.method)){let body="";for await(const chunk of req){body+=chunk}req.body=fn(body)}};const json=()=>async(req,_res,next)=>{await parsec(x=>JSON.parse(x.toString()))(req,_res);next==null?void 0:next()};const raw=()=>async(req,_res,next)=>{await parsec(x=>x)(req,_res);next==null?void 0:next()};const text=()=>async(req,_res,next)=>{await parsec(x=>x.toString())(req,_res);next==null?void 0:next()};const urlencoded=()=>async(req,_res,next)=>{await parsec(x=>parse(x.toString()))(req,_res);next==null?void 0:next()};export{parsec,json,raw,text,urlencoded};
